package collections;

public class WrapperClass {
	
	/*
	 * Wrapper Class - Primitive data type enclosed with class -Converting data from one format to another
	 * 
	 * int - Integer
	 * float - FLOAT
	 * double - DOUBLE
	 * boolean - BOOLEAN
	 * char - CHARACTER
	 * 
	 * 
	 * 
	 * 
	 * 
	 * 
	 * 
	 */

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		String id = "12345";
		
	int num = 	Integer.parseInt(id);
	
	
	System.out.println(num);
	
	
	int zip = 35801;
	
	String acc = String.valueOf(zip);
	
	System.out.println(acc);
		
		
		
	}

}
