package typeCasting;

public class typeC {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Type Casting - convert data from one format to another 
		
		//Widening casting = small data in big container - up Casting
		//Narrowing casting = large data in small container - down Casting
		
		//upCasting 
		
		// primitive data types and objects
		
		//1.Primitive Data Types
		
		int a = 15; // int , long
		
		long b = a; // up casting
		
		int c = (int) b; // down casting
		
		float d = 123.45f;
		
		double e = d; // up casting
		
		float f =(float) e;
		
		
		System.out.println(f);
		

	}

}
